<script>

import {weather_get,weather_t_get} from '../request/axios'
import { Toast } from 'vant'
  export default {
    name: 'No2',
async created () {
 this.wea_get();
  this.wea_t_get();

  this.city_name_change=''

    },
    data(){
      return{
        weather_info:{},
      year_month_day:'',
        hour_min:'',
        pm_num:0,
        tomorrow_year_month_day:0,
        weather_t:{},
        city_name_change:'武汉',

      }
    },
    watch:{
    // async  '$store.wea.state.city_name'(){
    //    console.log(this.$store.wea.state.city_name)
    //     this.city_name_change=this.$store.wea.state.city_name
    // const result= await  weather_get(this.city_name_change)
    // console.log(result)
    // }

    },
    methods:{
   async wea_get(){
        this.weather_info =(await weather_get(this.city_name_change)).data;
        let date=new Date();
        let date_str = JSON.stringify(date);
        this.year_month_day= date_str.substr(1,10)
        let year=date.getFullYear()
        let month= date.getMonth()+1
        let day=date.getDate()+1
        this.tomorrow_year_month_day=year+"-"+month+"-"+day

        console.log(this.weather_info)

        let hour =date.getHours()
        let minutes=date.getMinutes()
        this.hour_min=hour+':'+minutes
        this.pm_num= parseInt(this.weather_info.pm25)
        console.log(this.pm_num)
        // console.log(this.$store.state.wea.city_name)
      },
    async  wea_t_get(){
        this.weather_t=(await weather_t_get(this.city_name_change)).data.data.daily_forecast[1]
      console.log(this.weather_t)
      },
      Dialog_confirm(){
        if (this.city_name_change.trim()!=''){
           this.wea_get()
          this.wea_t_get()
          this.city_name_change=''
        }else {
          Toast.fail('内容不得为空');
        }
      }
    }
  }
</script>

